import { useEffect, useState } from 'react';
import { useAuth } from '../context/AuthContext';
import doctorService from '../services/doctorService';

const StudentDashboard = () => {
  const { user } = useAuth();
  const [visits, setVisits] = useState([]);
  const [form, setForm] = useState({ doctorName: '', time: '', reason: '' });

  useEffect(() => {
    if (user) fetchMyVisits();
  }, [user]);

  const fetchMyVisits = async () => {
    try {
      const res = await doctorService.myVisits();
      setVisits(res);
    } catch (err) {
      console.error(err);
    }
  };

  const handleInputChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await doctorService.scheduleVisit({ ...form, student: user.id });
      setForm({ doctorName: '', time: '', reason: '' });
      fetchMyVisits();
      alert('Visit scheduled successfully');
    } catch (err) {
      console.error(err);
      alert('Failed to schedule visit');
    }
  };

  return (
    <div className="student-dashboard">
      <h1>Welcome, {user?.name}!</h1>
      <p>Manage your doctor visits and hostel activities.</p>

      <div className="dashboard-section">
        <h2>Schedule Doctor Visit</h2>
        <form onSubmit={handleSubmit} className="schedule-form">
          <div>
            <label>Doctor Name:</label>
            <input type="text" name="doctorName" value={form.doctorName} onChange={handleInputChange} required />
          </div>
          <div>
            <label>Time:</label>
            <input type="datetime-local" name="time" value={form.time} onChange={handleInputChange} required />
          </div>
          <div>
            <label>Reason:</label>
            <textarea name="reason" value={form.reason} onChange={handleInputChange} required></textarea>
          </div>
          <button type="submit">Schedule Visit</button>
        </form>
      </div>

      <div className="dashboard-section">
        <h2>My Doctor Visits</h2>
        <div className="panel-list">
          {visits.length === 0 ? (
            <p>No visits scheduled.</p>
          ) : (
            visits.map(v => (
              <div key={v._id} className="card">
                <div>Doctor: {v.doctorName}</div>
                <div>Time: {new Date(v.time).toLocaleString()}</div>
                <div>Reason: {v.reason}</div>
                <div>Status: {v.status}</div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default StudentDashboard;
